import email
import hashlib
import quopri, base64
from StringIO import StringIO

def parse_basic_info(email_object):
    sender = email_object['From']
    recipients = email_object['To']
    subject = email_object['Subject']
    return {"sender": sender, "recipients": recipients, "subject": subject}

def get_attachments(email_object):
    attachments = []
    MD5s = ""
    if email_object.is_multipart():
        for part in email_object.walk():
            if part.get("Content-Disposition"):
              attachments.append({"filename": part.get_filename(), "base64": part.get_payload()})
              file_object = StringIO(base64.b64decode(part.get_payload()))
              MD5_text = get_MD5(file_object)
              if MD5s == "":
                MD5s = str(MD5_text)
              else:
                MD5s = MD5s + ", " + str(MD5_text)
    return attachments, MD5s

def get_headers(email_object):
    parser = email.parser.HeaderParser()
    headers = parser.parsestr(email_object.as_string())
    fullheaders = ''.join(h[0] +": " + h[1] + "\n" for h in headers.items())
    return fullheaders

